# LENDO O ARQUIVO CSV E SALVANDO EM EXCEL

import pandas as pd
import os
import tkinter as tk
from tkinter import filedialog

# LER O ARQUIVO CSV

df = pd.read_csv('Flash.csv', encoding='latin1')

# SOLICITAR ENTRADA DO USUÁRIO

ano = int(input("Digite o ano desejado: "))
sigla_uf = input("Digite a sigla da UF desejada: ")

# FILTRAR O DATAFREME COM BASE NAS ENTRADAS DO USUÁRIO

df_filtrado = df[(df['ano'] == ano) & (df['sigla_uf'] == sigla_uf)]

# EXIBIR OS RESULTADOS DA PESQUISA

if len(df_filtrado) > 0:
    print("Resultados encontrados:")
    print(df_filtrado)

# SOLICITAR AO USUÁRIO SE DESEJA SALVAR OS RESULTADOS EM UM NOVO ARQUIVO EXCEL
    
    salvar = input("Deseja salvar os resultados em um novo arquivo Excel? (S/N): ")
    if salvar.lower() == 's':
        nome_arquivo = input("Digite o nome do arquivo Excel para salvar: ")

        root = tk.Tk()
        root.withdraw()
        pasta_salvar = filedialog.askdirectory(title="Selecione o diretório para salvar o arquivo")

        if pasta_salvar:
            caminho_completo = os.path.join(pasta_salvar, nome_arquivo + ".xlsx")
            df_filtrado.to_excel(caminho_completo, index=False)
            print("Resultados salvos com sucesso!")
        else:
            print("Nenhum diretório selecionado. Os resultados não foram salvos.")
else:
    print("Nenhum resultado encontrado para os critérios informados.")    


