import pandas as pd
import matplotlib.pyplot as plt


# In[4]:


mapbiomas_data = pd.read_csv('Flash.csv')


# In[14]:


# AGRUPAR POR ANO E CONTAR O NÚMERO DE OCORRÊNCIAS EM CADA ANO
occurrences_by_year = mapbiomas_data.groupby('ano').size()

# PLOTAR GRÁFICO DE LINHAS
plt.plot(occurrences_by_year.index, occurrences_by_year.values)
plt.xlabel('Ano')
plt.ylabel('Contagem')
plt.title('Número de Ocorrências por Ano')
plt.show()


